import React, { useState, useEffect } from 'react';
import PrescriptionModal from '../components/PrescriptionModal';
import VisitModal from '../components/VisitModal';

const API_URL = '/api/patients';

const PatientDetails = ({ patientId, onBack }) => {
  const [patient, setPatient] = useState(null);
  const [activeTab, setActiveTab] = useState('overview');
  const [showPrescriptionModal, setShowPrescriptionModal] = useState(false);
  const [editingPrescription, setEditingPrescription] = useState(null);
  const [showVisitModal, setShowVisitModal] = useState(false);
  const [editingVisit, setEditingVisit] = useState(null);

  // Fetch patient from backend
  useEffect(() => {
    fetch(`${API_URL}/${patientId}`)
      .then(res => res.json())
      .then(setPatient);
  }, [patientId]);

  // Save patient to backend
  const savePatient = async (updated) => {
    const res = await fetch(`${API_URL}/${patient._id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(updated)
    });
    if (!res.ok) throw new Error('Failed to update patient');
    const data = await res.json();
    setPatient(data);
  };

  // Prescription handlers
  const handleSavePrescription = async (prescription) => {
    let updatedPrescriptions;
    if (editingPrescription !== null) {
      updatedPrescriptions = patient.prescriptions.map((p, idx) =>
        idx === editingPrescription ? prescription : p
      );
    } else {
      updatedPrescriptions = [...(patient.prescriptions || []), prescription];
    }
    const updated = { ...patient, prescriptions: updatedPrescriptions };
    await savePatient(updated);
    setShowPrescriptionModal(false);
    setEditingPrescription(null);
  };

  const handleDeletePrescription = async (idx) => {
    if (window.confirm('Are you sure you want to delete this prescription?')) {
      const updatedPrescriptions = patient.prescriptions.filter((_, i) => i !== idx);
      const updated = { ...patient, prescriptions: updatedPrescriptions };
      await savePatient(updated);
    }
  };

  // Visit handlers
  const handleSaveVisit = async (visit) => {
    let updatedVisits;
    if (editingVisit !== null) {
      updatedVisits = patient.visits.map((v, idx) =>
        idx === editingVisit ? visit : v
      );
    } else {
      updatedVisits = [...(patient.visits || []), visit];
    }
    const updated = { ...patient, visits: updatedVisits };
    await savePatient(updated);
    setShowVisitModal(false);
    setEditingVisit(null);
  };

  const handleDeleteVisit = async (idx) => {
    if (window.confirm('Are you sure you want to delete this visit?')) {
      const updatedVisits = patient.visits.filter((_, i) => i !== idx);
      const updated = { ...patient, visits: updatedVisits };
      await savePatient(updated);
    }
  };

  if (!patient) return <div>Loading...</div>;

  return (
    <div>
      <button onClick={onBack}>Back</button>
      <h2>{patient.firstName} {patient.lastName}</h2>
      <ul>
        <li onClick={() => setActiveTab('overview')}>Overview</li>
        <li onClick={() => setActiveTab('visits')}>Visits</li>
        <li onClick={() => setActiveTab('prescriptions')}>Prescriptions</li>
      </ul>

      {activeTab === 'overview' && (
        <div>
          <div>Phone: {patient.phone}</div>
          <div>Email: {patient.email}</div>
          <div>Address: {patient.address}</div>
        </div>
      )}

      {activeTab === 'visits' && (
        <div>
          <button onClick={() => { setEditingVisit(null); setShowVisitModal(true); }}>Add Visit</button>
          {(patient.visits || []).map((visit, idx) => (
            <div key={idx}>
              <span>{visit.date} - {visit.reason}</span>
              <button onClick={() => { setEditingVisit(idx); setShowVisitModal(true); }}>Edit</button>
              <button onClick={() => handleDeleteVisit(idx)}>Delete</button>
            </div>
          ))}
          <VisitModal
            show={showVisitModal}
            onClose={() => { setShowVisitModal(false); setEditingVisit(null); }}
            onSave={handleSaveVisit}
            visit={editingVisit !== null ? patient.visits[editingVisit] : null}
          />
        </div>
      )}

      {activeTab === 'prescriptions' && (
        <div>
          <button onClick={() => { setEditingPrescription(null); setShowPrescriptionModal(true); }}>Add Prescription</button>
          {(patient.prescriptions || []).map((pres, idx) => (
            <div key={idx}>
              <span>{pres.medicationName} - {pres.dosage}</span>
              <button onClick={() => { setEditingPrescription(idx); setShowPrescriptionModal(true); }}>Edit</button>
              <button onClick={() => handleDeletePrescription(idx)}>Delete</button>
            </div>
          ))}
          <PrescriptionModal
            show={showPrescriptionModal}
            onClose={() => { setShowPrescriptionModal(false); setEditingPrescription(null); }}
            onSave={handleSavePrescription}
            prescription={editingPrescription !== null ? patient.prescriptions[editingPrescription] : null}
          />
        </div>
      )}
    </div>
  );
};

export default PatientDetails;
