import React, { useEffect, useState } from 'react';
import axios from 'axios';
import PatientList from '../components/PatientList';
import PatientModal from '../components/PatientModal';
import PatientDetails from './PatientDetails';

const PAGE_SIZE = 10; // Number of patients per page

const Patients = () => {
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedPatient, setSelectedPatient] = useState(null);
  const [showPatientModal, setShowPatientModal] = useState(false);
  const [editingPatient, setEditingPatient] = useState(null);

  const [searchTerm, setSearchTerm] = useState('');
  const [searchCondition, setSearchCondition] = useState('');
  const [searchVisitDate, setSearchVisitDate] = useState('');
  const [sortField, setSortField] = useState('createdAt');
  const [sortOrder, setSortOrder] = useState('desc');
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  const fetchPatients = async () => {
    setLoading(true);
    try {
      const params = {
        search: searchTerm,
        condition: searchCondition,
        visitDate: searchVisitDate,
        sortField,
        sortOrder,
        page,
        pageSize: PAGE_SIZE,
      };
      const res = await axios.get('http://localhost:5000/api/patients/advanced-search', { params });
      setPatients(res.data.patients);
      setTotalPages(res.data.totalPages);
    } catch (err) {
      alert('Error fetching patients.');
      setPatients([]);
      setTotalPages(1);
    }
    setLoading(false);
  };
  useEffect(() => {
    fetchPatients();
    // eslint-disable-next-line
  }, [searchTerm, searchCondition, searchVisitDate, sortField, sortOrder, page]);

  const handleDeletePatient = async (id) => {
    if (!window.confirm('Are you sure?')) return;
    try {
      await axios.delete(`http://localhost:5000/api/patients/${id}`);
      setPatients(patients.filter(p => p._id !== id));
      if (selectedPatient && selectedPatient._id === id) setSelectedPatient(null);
    } catch (err) {
      alert('Error deleting patient');
    }
  };

  const handleSavePatient = async (patientData) => {
    if (editingPatient) {
      try {
        const res = await axios.put(`http://localhost:5000/api/patients/${editingPatient._id}`, patientData);
        setPatients(patients.map(p => p._id === editingPatient._id ? res.data : p));
        setSelectedPatient(res.data); // update selected patient info
      } catch {
        alert('Error updating patient');
      }
    } else {
      try {
        const res = await axios.post('http://localhost:5000/api/patients', patientData);
        setPatients([...patients, res.data]);
      } catch {
        alert('Error adding patient');
      }
    }
    setShowPatientModal(false);
    setEditingPatient(null);
  };

  const updatePatientData = (updatedData) => {
    // update patient in list and if selected, update selectedPatient state
    fetchPatients();
    if (selectedPatient && selectedPatient._id === updatedData._id) setSelectedPatient(updatedData);
  };
 const handlePrevPage = () => setPage(page > 1 ? page - 1 : 1);
  const handleNextPage = () => setPage(page < totalPages ? page + 1 : totalPages);

  const filteredPatients = patients.filter(patient =>
    patient.firstName.toLowerCase().includes(searchTerm.toLowerCase()) ||
    patient.lastName.toLowerCase().includes(searchTerm.toLowerCase()) ||
    patient.phone.includes(searchTerm)
  );

  if (selectedPatient) {
    return (
      <PatientDetails
        patient={selectedPatient}
        onBack={() => setSelectedPatient(null)}
        onUpdatePatient={updatePatientData}
      />
    );
  }

 return (
    <div>
      <h1>Patient Records</h1>
      <h2 style={{ fontSize: '1.5rem' }}>All Patients</h2>
      <div className="d-flex flex-wrap align-items-center mb-3" style={{ gap: 10 }}>
        <input
          type="text"
          placeholder="Search by ID, Name..."
          value={searchTerm}
          onChange={(e) => { setSearchTerm(e.target.value); setPage(1); }}
          className="form-control"
          style={{ width: 200 }}
        />
        <input
          type="text"
          placeholder="Condition"
          value={searchCondition}
          onChange={(e) => { setSearchCondition(e.target.value); setPage(1); }}
          className="form-control"
          style={{ width: 140 }}
        />
        <input
          type="date"
          placeholder="Visit Date"
          value={searchVisitDate}
          onChange={(e) => { setSearchVisitDate(e.target.value); setPage(1); }}
          className="form-control"
          style={{ width: 150 }}
        />
        <select
          className="form-select"
          value={sortField}
          onChange={e => { setSortField(e.target.value); setPage(1); }}
          style={{ width: 120 }}
        >
          <option value="createdAt">Sort: Newest</option>
          <option value="firstName">Sort: Name</option>
          <option value="dob">Sort: DOB</option>
        </select>
        <select
          className="form-select"
          value={sortOrder}
          onChange={e => { setSortOrder(e.target.value); setPage(1); }}
          style={{ width: 110 }}
        >
          <option value="desc">Desc</option>
          <option value="asc">Asc</option>
        </select>
        <button className="btn btn-primary" onClick={() => { setEditingPatient(null); setShowPatientModal(true); }}>
          Add Patient
        </button>
      </div>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <>
          <PatientList
            patients={patients}
            onView={setSelectedPatient}
            onEdit={(patient) => { setEditingPatient(patient); setShowPatientModal(true); }}
            onDelete={handleDeletePatient}
          />
          <div className="d-flex justify-content-between align-items-center mt-2">
            <button className="btn btn-secondary" onClick={handlePrevPage} disabled={page === 1}>Previous</button>
            <span>Page {page} of {totalPages}</span>
            <button className="btn btn-secondary" onClick={handleNextPage} disabled={page === totalPages}>Next</button>
          </div>
        </>
      )}
      <PatientModal
        show={showPatientModal}
        onClose={() => setShowPatientModal(false)}
        onSave={handleSavePatient}
        patient={editingPatient}
      />
    </div>
  );
};

export default Patients;
