import React, { useState, useRef, useEffect } from "react";
import { auth, googleProvider } from "../firebase";
import {
  signInWithPopup,
  signInWithEmailAndPassword,
  createUserWithEmailAndPassword,
  signInWithPhoneNumber,
  RecaptchaVerifier,
  signOut,
} from "firebase/auth";
import "./AccountSettings.css";

function AccountSettings() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [user, setUser] = useState(auth.currentUser);
  const [phone, setPhone] = useState("");
  const [otp, setOtp] = useState("");
  const [confirmationResult, setConfirmationResult] = useState(null);
  const [error, setError] = useState("");
  const [showEmail, setShowEmail] = useState(false);
  const [showPhone, setShowPhone] = useState(false);
  const [isSignUp, setIsSignUp] = useState(false);

  const recaptchaVerifier = useRef(null);

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged(setUser);
    return () => unsubscribe();
  }, []);

  // Only create RecaptchaVerifier when needed and only once
  const setupRecaptcha = () => {
    if (!recaptchaVerifier.current) {
      recaptchaVerifier.current = new RecaptchaVerifier(
        "recaptcha-container",
        { size: "invisible" },
        auth
      );
    }
    return recaptchaVerifier.current;
  };

  const handleGoogle = async () => {
    setError("");
    try {
      await signInWithPopup(auth, googleProvider);
    } catch (err) {
      setError(err.message);
    }
  };

  const handleEmailSignIn = async (e) => {
    e.preventDefault();
    setError("");
    try {
      await signInWithEmailAndPassword(auth, email, password);
    } catch (err) {
      setError(err.message);
    }
  };

  const handleEmailSignUp = async (e) => {
    e.preventDefault();
    setError("");
    try {
      await createUserWithEmailAndPassword(auth, email, password);
    } catch (err) {
      setError(err.message);
    }
  };

  const handleSignOut = async () => {
    setError("");
    try {
      await signOut(auth);
    } catch (err) {
      setError(err.message);
    }
  };

  // Phone Auth Integration
const handlePhoneSignIn = async (e) => {
  e.preventDefault();
  setError("");
  try {
    // Only create once and always on window
    if (!window.recaptchaVerifier) {
      window.recaptchaVerifier = new RecaptchaVerifier(
        "recaptcha-container",
        { size: "invisible" },
        auth
      );
      await window.recaptchaVerifier.render();
    }
    const appVerifier = window.recaptchaVerifier;
    const confirmation = await signInWithPhoneNumber(auth, phone, appVerifier);
    setConfirmationResult(confirmation);
  } catch (err) {
    setError(err.message);
  }
};

  const handleVerifyOtp = async (e) => {
    e.preventDefault();
    setError("");
    try {
      if (confirmationResult) {
        await confirmationResult.confirm(otp);
        setOtp("");
        setPhone("");
        setConfirmationResult(null);
      }
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div className="account-section">
      <div className="account-card">
        <h2 className="account-title">Sign In to Care Track</h2>
        {error && <div className="account-error">{error}</div>}
        {user ? (
          <div className="account-user-info">
            <div className="account-avatar">
              {user.photoURL ? (
                <img src={user.photoURL} alt="avatar" />
              ) : (
                <span>{user.email?.[0]?.toUpperCase() || user.phoneNumber?.slice(-2) || "U"}</span>
              )}
            </div>
            <p className="account-user-email">
              {user.email || user.phoneNumber || user.displayName}
            </p>
            <button className="account-btn signout-btn" onClick={handleSignOut}>
              Sign Out
            </button>
          </div>
        ) : (
          <div className="account-signin-options">
            <button className="account-btn google-btn" onClick={handleGoogle}>
              <img
                src="https://www.gstatic.com/firebasejs/ui/2.0.0/images/auth/google.svg"
                alt="Google"
                className="account-btn-icon"
              />
              Sign in with Google
            </button>
            <button
              className="account-btn email-btn"
              onClick={() => {
                setShowEmail(!showEmail);
                setShowPhone(false);
                setIsSignUp(false);
                setError("");
              }}
            >
              <img
                src="https://img.icons8.com/ios-filled/50/000000/new-post.png"
                alt="Email"
                className="account-btn-icon"
              />
              Sign in with Email
            </button>
            <button
              className="account-btn phone-btn"
              onClick={() => {
                setShowPhone(!showPhone);
                setShowEmail(false);
                setError("");
              }}
            >
              <img
                src="https://img.icons8.com/ios-filled/50/000000/phone.png"
                alt="Phone"
                className="account-btn-icon"
              />
              Sign in with Phone
            </button>

            {/* Email Form */}
            {showEmail && (
              <form
                className="account-form"
                onSubmit={isSignUp ? handleEmailSignUp : handleEmailSignIn}
              >
                <input
                  type="email"
                  placeholder="Email"
                  value={email}
                  onChange={e => setEmail(e.target.value)}
                  required
                />
                <input
                  type="password"
                  placeholder="Password"
                  value={password}
                  onChange={e => setPassword(e.target.value)}
                  required
                />
                <button className="account-btn submit-btn" type="submit">
                  {isSignUp ? "Sign up" : "Sign in"}
                </button>
                <div className="account-form-switch">
                  {isSignUp ? (
                    <span>
                      Already have an account?{" "}
                      <button
                        type="button"
                        onClick={() => setIsSignUp(false)}
                        className="account-link"
                      >
                        Sign in
                      </button>
                    </span>
                  ) : (
                    <span>
                      New here?{" "}
                      <button
                        type="button"
                        onClick={() => setIsSignUp(true)}
                        className="account-link"
                      >
                        Sign up
                      </button>
                    </span>
                  )}
                </div>
              </form>
            )}

            {/* Phone Form */}
            {showPhone && (
              <form
                className="account-form"
                onSubmit={confirmationResult ? handleVerifyOtp : handlePhoneSignIn}
              >
                <input
                  type="tel"
                  placeholder="Phone number (+1234567890)"
                  value={phone}
                  onChange={e => setPhone(e.target.value)}
                  required={!confirmationResult}
                  disabled={!!confirmationResult}
                />
                {confirmationResult && (
                  <input
                    type="text"
                    placeholder="Enter OTP"
                    value={otp}
                    onChange={e => setOtp(e.target.value)}
                    required
                  />
                )}
                <button className="account-btn submit-btn" type="submit">
                  {confirmationResult ? "Verify OTP" : "Send OTP"}
                </button>
                <div id="recaptcha-container"></div>
              </form>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default AccountSettings;
