import React from 'react';

// Helper to format date as dd-mm-yyyy
const formatDDMMYYYY = (dateStr) => {
  if (!dateStr) return '';
  const d = new Date(dateStr);
  if (isNaN(d)) return '';
  const day = String(d.getDate()).padStart(2, '0');
  const month = String(d.getMonth() + 1).padStart(2, '0');
  const year = d.getFullYear();
  return `${day}-${month}-${year}`;
};

const PatientList = ({ patients, onView, onEdit, onDelete }) => (
  <table className="table table-striped">
    <thead>
      <tr>
        <th>Name</th>
        <th>Gender</th>
        <th>DOB</th>
        <th>Phone</th>
        <th>Last Visit</th>
        <th>Actions</th>
      </tr>
    </thead>
    <tbody>
      {patients.map(patient => (
        <tr key={patient._id}>
          <td>{patient.firstName} {patient.lastName}</td>
          <td>{patient.gender}</td>
          <td>{patient.dob ? formatDDMMYYYY(patient.dob) : 'N/A'}</td>
          <td>{patient.phone}</td>
          <td>
            {patient.lastVisit && !isNaN(new Date(patient.lastVisit))
              ? formatDDMMYYYY(patient.lastVisit)
              : 'N/A'}
          </td>
          <td>
            <button className="btn btn-sm btn-info me-1" onClick={() => onView(patient)}>View</button>
            <button className="btn btn-sm btn-warning me-1" onClick={() => onEdit(patient)}>Edit</button>
            <button className="btn btn-sm btn-danger" onClick={() => onDelete(patient._id)}>Delete</button>
          </td>
        </tr>
      ))}
    </tbody>
  </table>
);

export default PatientList;
