import React from 'react';

function Navigation({ currentView, setCurrentView, theme, setTheme }) {
  return (
    <nav className="navigation">
      <button
        className={currentView === 'dashboard' ? 'active' : ''}
        onClick={() => setCurrentView('dashboard')}
      >
        Dashboard
      </button>
      <button
        className={currentView === 'patients' ? 'active' : ''}
        onClick={() => setCurrentView('patients')}
      >
        Patients
      </button>
      <button
        className={currentView === 'analytics' ? 'active' : ''}
        onClick={() => setCurrentView('analytics')}
      >
        Analytics
      </button>
      <button
        className={currentView === 'settings' ? 'active' : ''}
        onClick={() => setCurrentView('settings')}
      >
        Settings
      </button>
      {/* Only this button is new */}
      <button
        className={currentView === 'account' ? 'active' : ''}
        onClick={() => setCurrentView('account')}
      >
        Account
      </button>
      {/* Keep your original theme toggle or any other UI here */}
      <span style={{ marginLeft: 'auto' }}>
        <label>
          <input
            type="checkbox"
            checked={theme === 'dark'}
            onChange={() => setTheme(theme === 'dark' ? 'light' : 'dark')}
          />
          Dark Mode
        </label>
      </span>
    </nav>
  );
}

export default Navigation;
