import React, { useState } from 'react';
import PrescriptionModal from '../components/PrescriptionModal';
import VisitModal from '../components/VisitModal';
import MedicalHistoryModal from '../components/MedicalHistoryModal';

const PatientDetails = ({ patient, onBack, onUpdatePatient, onEditPatient, onDeletePatient }) => {
  const [prescriptions, setPrescriptions] = useState(patient.prescriptions || []);
  const [visits, setVisits] = useState(patient.visits || []);
  const [medicalHistory, setMedicalHistory] = useState(patient.medicalHistory || []);
  const [showPrescriptionModal, setShowPrescriptionModal] = useState(false);
  const [showVisitModal, setShowVisitModal] = useState(false);
  const [showMedicalHistoryModal, setShowMedicalHistoryModal] = useState(false);
  const [currentHistoryItem, setCurrentHistoryItem] = useState(null);
  const [activeTab, setActiveTab] = useState('overview');

  const addPrescription = (prescription) => {
    const updatedPrescriptions = [...prescriptions, prescription];
    setPrescriptions(updatedPrescriptions);
    const updatedPatient = { ...patient, prescriptions: updatedPrescriptions };
    onUpdatePatient(updatedPatient);
  };

  const deletePrescription = (index) => {
    if (window.confirm('Delete this prescription?')) {
      const updatedPrescriptions = prescriptions.filter((_, i) => i !== index);
      setPrescriptions(updatedPrescriptions);
      const updatedPatient = { ...patient, prescriptions: updatedPrescriptions };
      onUpdatePatient(updatedPatient);
    }
  };

  const addVisit = (visit) => {
    const updatedVisits = [...visits, visit];
    setVisits(updatedVisits);
    const updatedPatient = { ...patient, visits: updatedVisits };
    onUpdatePatient(updatedPatient);
  };

  const deleteVisit = (index) => {
    if (window.confirm('Delete this visit?')) {
      const updatedVisits = visits.filter((_, i) => i !== index);
      setVisits(updatedVisits);
      const updatedPatient = { ...patient, visits: updatedVisits };
      onUpdatePatient(updatedPatient);
    }
  };

  const addMedicalHistory = (historyItem) => {
    const updatedMedicalHistory = [...medicalHistory, historyItem];
    setMedicalHistory(updatedMedicalHistory);
    const updatedPatient = { ...patient, medicalHistory: updatedMedicalHistory };
    onUpdatePatient(updatedPatient);
  };

  const deleteMedicalHistory = (index) => {
    if (window.confirm('Delete this medical history item?')) {
      const updatedMedicalHistory = medicalHistory.filter((_, i) => i !== index);
      setMedicalHistory(updatedMedicalHistory);
      const updatedPatient = { ...patient, medicalHistory: updatedMedicalHistory };
      onUpdatePatient(updatedPatient);
    }
  };

  const handleEditPatient = () => {
    onEditPatient(patient);
  };

  const handleDeletePatient = () => {
    if (window.confirm('Are you sure you want to delete this patient?')) {
      onDeletePatient(patient._id);
    }
  };

  return (
    <div className="container mt-4">
      <button onClick={onBack} className="btn btn-secondary mb-3">‚Üê Back to Patients</button>
      <h1 style={{ fontSize: '1.8rem' }}>{patient.firstName} {patient.lastName}</h1>
      <p><strong>Patient ID:</strong> {patient.patientId} | <strong>Gender:</strong> {patient.gender} | <strong>Age:</strong> {new Date().getFullYear() - new Date(patient.dob).getFullYear()} years</p>

      <div className="mb-3">
        <button className="btn btn-primary me-2" onClick={() => setShowVisitModal(true)}>Add Visit</button>
        <button className="btn btn-info me-2" onClick={handleEditPatient}>Edit Data</button>
        <button className="btn btn-danger" onClick={handleDeletePatient}>Delete Patient</button>
      </div>

      <ul className="nav nav-tabs">
        <li className="nav-item">
          <button className={`nav-link ${activeTab === 'overview' ? 'active' : ''}`} onClick={() => setActiveTab('overview')}>Overview</button>
        </li>
        <li className="nav-item">
          <button className={`nav-link ${activeTab === 'medicalHistory' ? 'active' : ''}`} onClick={() => setActiveTab('medicalHistory')}>Medical History</button>
        </li>
        <li className="nav-item">
          <button className={`nav-link ${activeTab === 'prescriptions' ? 'active' : ''}`} onClick={() => setActiveTab('prescriptions')}>Prescriptions</button>
        </li>
        <li className="nav-item">
          <button className={`nav-link ${activeTab === 'visits' ? 'active' : ''}`} onClick={() => setActiveTab('visits')}>Patient Visits</button>
        </li>
      </ul>

      <div className="tab-content mt-3">
        {activeTab === 'overview' && (
          <div>
            <h3>Contact Information</h3>
            <p><strong>Phone:</strong> {patient.phone}</p>
            <p><strong>Email:</strong> {patient.email}</p>
            <p><strong>Address:</strong> {patient.address}</p>
            <h3>Allergies</h3>
            <p>{patient.allergies || 'No allergies recorded.'}</p>
            <h3>Current Medications</h3>
            <p>{patient.medications || 'No current medications.'}</p>
          </div>
        )}

        {activeTab === 'medicalHistory' && (
          <div>
            <h3>Medical History</h3>
            <button className="btn btn-primary mb-3" onClick={() => { setCurrentHistoryItem(null); setShowMedicalHistoryModal(true); }}>Add Medical History</button>
            {medicalHistory.length > 0 ? (
              medicalHistory.map((history, index) => (
                <div key={index} className="mb-2 border rounded p-2">
                  <p><strong>Condition:</strong> {history.condition}</p>
                  <p><strong>Diagnosis Date:</strong> {history.diagnosisDate || 'N/A'}</p>
                  <p><strong>Treatment:</strong> {history.treatment || 'N/A'}</p>
                  <p><strong>Notes:</strong> {history.notes || 'N/A'}</p>
                  <button className="btn btn-danger btn-sm" onClick={() => deleteMedicalHistory(index)}>Delete</button>
                  <button className="btn btn-warning btn-sm ms-2" onClick={() => { setCurrentHistoryItem(history); setShowMedicalHistoryModal(true); }}>Edit</button>
                </div>
              ))
            ) : (
              <p>No medical history available.</p>
            )}
          </div>
        )}

        {activeTab === 'prescriptions' && (
          <div>
            <h3>Prescriptions</h3>
            <button className="btn btn-primary mb-3" onClick={() => setShowPrescriptionModal(true)}>Add Prescription</button>
            {prescriptions.length > 0 ? prescriptions.map((prescription, index) => (
              <div key={index}>
                <p>{prescription.medication} - {prescription.dosage} ({prescription.frequency})</p>
                <button className="btn btn-danger btn-sm" onClick={() => deletePrescription(index)}>Delete</button>
                <button className="btn btn-warning btn-sm ms-2" onClick={() => { setShowPrescriptionModal(true); setCurrentHistoryItem(prescription); }}>Edit</button>
              </div>
            )) : <p>No prescriptions available.</p>}
          </div>
        )}

        {activeTab === 'visits' && (
          <div>
            <h3>Patient Visits</h3>
            <button className="btn btn-primary mb-3" onClick={() => setShowVisitModal(true)}>Add Visit</button>
            {visits.length > 0 ? visits.map((visit, index) => (
              <div key={index}>
                <p>{visit.reason} on {visit.date}</p>
                <button className="btn btn-danger btn-sm" onClick={() => deleteVisit(index)}>Delete</button>
                <button className="btn btn-warning btn-sm ms-2" onClick={() => { setShowVisitModal(true); setCurrentHistoryItem(visit); }}>Edit</button>
              </div>
            )) : <p>No visits recorded.</p>}
          </div>
        )}
      </div>

      {/* Prescription Modal */}
      {showPrescriptionModal && (
        <PrescriptionModal 
          show={showPrescriptionModal} 
          onClose={() => setShowPrescriptionModal(false)} 
          onSave={addPrescription} 
          prescriptionItem={currentHistoryItem} 
        />
      )}

      {/* Visit Modal */}
      {showVisitModal && (
        <VisitModal 
          show={showVisitModal} 
          onClose={() => setShowVisitModal(false)} 
          onSave={addVisit} 
          visitItem={currentHistoryItem} 
        />
      )}

      {/* Medical History Modal */}
      {showMedicalHistoryModal && (
        <MedicalHistoryModal 
          show={showMedicalHistoryModal} 
          onClose={() => setShowMedicalHistoryModal(false)} 
          onSave={addMedicalHistory} 
          historyItem={currentHistoryItem} 
        />
      )}
    </div>
  );
};

export default PatientDetails;
