import React from 'react';

const Dashboard = ({ patients }) => {
  const totalPatients = patients.length;
  const upcomingAppointments = patients.filter(p => p.visits && p.visits.some(v => new Date(v.date) > new Date())).length;
  const patientsWithPrescriptions = patients.filter(p => p.prescriptions && p.prescriptions.length > 0).length;

  return (
    <div>
      <h1>Dashboard</h1>
      <p>Welcome to the CareTrack Patient Management System!</p>
      <p>Here you can manage your patients, view analytics, and adjust settings.</p>
      <div id="dashboardView">
        <div className="analytics-container">
          <div className="analytics-card">
            <h2>Patient Statistics</h2>
            <p>Total Patients: <span id="totalPatients">{totalPatients}</span></p>
            <p>New Patients This Month: <span id="newPatients">0</span></p>
            <p>Upcoming Appointments: <span id="upcomingAppointments">{upcomingAppointments}</span></p>
            <p>Patients with Prescriptions: <span id="patientsWithPrescriptions">{patientsWithPrescriptions}</span></p>
          </div>
          <div className="analytics-card">
            <h2>Recent Activity</h2>
            <div id="recentActivity">
              <p>No recent activity</p>
              <button className="btn btn-primary">Refresh Patient Data</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
