import React, { useEffect, useState } from 'react';

const formatDate = (dateStr) => {
  if (!dateStr) return 'N/A';
  const d = new Date(dateStr);
  if (isNaN(d.getTime())) return 'N/A'; // Fixed: Proper check for invalid date
  const day = String(d.getDate()).padStart(2, '0');
  const month = String(d.getMonth() + 1).padStart(2, '0');
  const year = d.getFullYear();
  return `${day}-${month}-${year}`;
};

const PatientList = ({ patients, onEdit, onDelete, onView }) => {
  const [updatedPatients, setUpdatedPatients] = useState(patients);

  useEffect(() => {
    setUpdatedPatients(patients); // Ensure the latest patients data is reflected
  }, [patients]);

  const getLastVisit = (patient) => {
    if (patient.lastVisit) return formatDate(patient.lastVisit);
    if (!Array.isArray(patient.visits) || patient.visits.length === 0) return 'N/A';
    
    const sorted = patient.visits
      .map(v => new Date(v.date))
      .filter(d => !isNaN(d.getTime())) // Fixed: Proper date filtering
      .sort((a, b) => b - a);
    
    return sorted.length > 0 ? formatDate(sorted[0]) : 'N/A';
  };

  return (
    <table className="table table-hover">
      <thead>
        <tr>
          <th>Patient ID</th>
          <th>Name</th>
          <th>Gender</th>
          <th>DOB</th>
          <th>Phone</th>
          <th>Last Visit</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        {updatedPatients.length === 0 ? (
          <tr><td colSpan="7">No patients found.</td></tr>
        ) : (
          updatedPatients.map((patient) => (
            <tr key={patient._id}>
              <td>{patient.patientId || 'N/A'}</td>
              <td>{patient.firstName} {patient.lastName}</td>
              <td>{patient.gender}</td>
              <td>{formatDate(patient.dob)}</td>
              <td>{patient.phone}</td>
              <td>{getLastVisit(patient)}</td> {/* Fixed: Using helper function correctly */}
              <td>
                <button className="btn btn-sm btn-info me-2" onClick={() => onView(patient)}>View</button>
                <button className="btn btn-sm btn-secondary me-2" onClick={() => onEdit(patient)}>Edit</button>
                <button className="btn btn-sm btn-danger" onClick={() => onDelete(patient._id)}>Delete</button>
              </td>
            </tr>
          ))
        )}
      </tbody>
    </table>
  );
};

export default PatientList;
